  #{userSex,jdbcType=VARCHAR}, #{orginOrganizationId,jdbcType=VARCHAR}, #{targetOrganizationId,jdbcType=VARCHAR}, 
  #{userNation,jdbcType=VARCHAR}, #{changeReason,jdbcType=VARCHAR}, 0,
  0, #{applicationDate,jdbcType=DATE}, #{birthday,jdbcType=DATE}
